<script>
    const humburgerMenu = document.getElementById('humburger-menu');
    const crossMenu = document.getElementById('cross-menu');
    const sidebar = document.querySelector('aside');
    humburgerMenu.addEventListener('click', () => {
        sidebar.classList.toggle('w-44');
        humburgerMenu.classList.toggle('hidden');
        crossMenu.classList.remove('hidden');
    });

    crossMenu.addEventListener('click', () => {
        sidebar.classList.toggle('w-44');
        crossMenu.classList.toggle('hidden');
        humburgerMenu.classList.remove('hidden');
    });
</script>